	return $n;
}


/**
* 读取csv文件的某一行数据
*/
function get_file_line2( $file_name, $line ){
  $n = 0;
  $handle = fopen($file_name,'r');
  if ($handle) {
    while (!feof($handle)) {
        ++$n;
        $out = fgets($handle, 4096);
        if($line==$n) break;
    }
    fclose($handle);
  }
  if( $line==$n) return $out;
  return false;
//	if ($line==$n)
//	{
//		print_csv($handle);
//	}
}
