//其他	Mobile/Trailer, Det W/Com Elements, Store W/Apt/offc
function propertyType_id($p)
{
	$p = trim($p);
	$id=0;
	switch ($p)
	{
		case 'Detached':
			$id=1;
			break;
		case 'Semi-Detached':
			$id=2;
			break;
		case 'Link':
			$id=2;
			break;
		case 'Duplex':
			$id=2;
			break;
		case 'Townhouse，Att∕Row∕Twnhouse':
			$id=3;
			break;
		case 'Triplex':
			$id=3;
			break;
		case 'Fourplex':
			$id=3;
			break;
		case 'Multiplex':
			$id=3;
			break;
		case 'Condo':
			$id=6;
			break;
		case 'Apartment':
			$id=6;
			break;	
		case 'Cottage':
			$id=7;
			break;
		case 'Rural Resid':
			$id=7;
			break;
		case 'Farm':
			$id=8;
			break;
		case 'Vacant Land':
			$id=9;
			break;
		case 'Mobile/Trailer':
			$id=10;
			break;
		case 'Det W/Com Elements':
			$id=10;
			break;
		case 'Store W/Apt/offc':
			$id=10;
			break;
		default:
			$id=10;
	}
	return $id;
}

