//转换大门朝向
function door_direction($d)
{
//（N-北，S-南，E-东，W-西）
	$chn='';
	switch ($d)
	{
		case 'N':
			$chn='北';
			break;
		case 'S':
			$chn='南';
			break;
		case 'E':
			$chn='东';
			break;
		case 'W':
			$chn='西';
			break;
	}
	return $chn;
}

